        public static Prompt GetPromptForDayOfYear(int dayOfYear)
        {
            // Logic to get a prompt based on day of year
            if (!AllPrompts.Any())
            {
                return new Prompt { Text = "What's on your mind today?" };
            }
            var index = (dayOfYear - 1) % AllPrompts.Count;
            return AllPrompts[index];
        }

        public static Prompt GetRandomPrompt()
        {
            if (!AllPrompts.Any()) return new Prompt { Text = "What's on your mind today?" };
            var randomIndex = new Random().Next(0, AllPrompts.Count);
            return AllPrompts[randomIndex];
        }

        public static List<Prompt> GetAllPrompts()
        {
            return AllPrompts;
        }
